package com.uzprogram.android.uzbek;import androidx.appcompat.app.AppCompatActivity;import androidx.appcompat.widget.Toolbar;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentManager;import androidx.fragment.app.FragmentPagerAdapter;import androidx.viewpager.widget.ViewPager;import android.os.Bundle;import com.google.android.material.tabs.TabLayout;public class MainActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        Toolbar toolbar = (Toolbar) findViewById(R.id.toolbar);        setSupportActionBar(toolbar);        SectionsPagerAdapter pagerAdapter = new SectionsPagerAdapter(getSupportFragmentManager());        ViewPager pager = (ViewPager) findViewById(R.id.pager);        pager.setAdapter(pagerAdapter);        TabLayout tabLayout = (TabLayout) findViewById(R.id.tabs);        tabLayout.setupWithViewPager(pager);    }    private class SectionsPagerAdapter extends FragmentPagerAdapter {        public SectionsPagerAdapter(FragmentManager fm) {            super(fm);        }        @Override        public int getCount() {            return 4;        }        @Override        public Fragment getItem(int position) {            switch (position) {                case 0:                    return new NumbersFragment();                case 1:                    return new FamilyFragment();                case 2:                    return new ColorsFragment();                case 3:                    return new PhrasesFragment();            }            return null;        }        @Override        public CharSequence getPageTitle(int position) {            switch (position) {                case 0:                    return getResources().getText(R.string.category_numbers);                case 1:                    return getResources().getText(R.string.category_family);                case 2:                    return getResources().getText(R.string.category_colors);                case 3:                    return getResources().getText(R.string.category_phrases);            }            return null;        }    }}